import keras
import pandas as pd
import numpy as np
import matplotlib.pyplot as plt
import cv2

from keras import optimizers
from keras.models import Sequential
from keras.layers import Dense, Conv2D, Flatten, MaxPooling2D, Activation, Dropout, UpSampling2D, Reshape, LeakyReLU, BatchNormalization
from os.path import exists
from sklearn.model_selection import train_test_split
from keras.models import model_from_json

SIGMA = 0.5
X_TRAIN_PATH = 'Data/X_train.npy'
Y_TRAIN_PATH = 'Data/Y_train.npy'
X_TEST_PATH = 'Data/X_test.npy'
Y_TEST_PATH = 'Data/Y_test.npy'
H = 96
W = 96


def plot_loss(hist, name, plt):
    '''
    RMSE_TF: if True, then RMSE is plotted with original scale
    '''
    loss = hist['loss']
    val_loss = hist['val_loss']

    plt.plot(loss, "--", linewidth=3, label="train:" + name)
    plt.plot(val_loss, linewidth=3, label="val:" + name)

    plt.legend()
    plt.grid()
    plt.yscale("log")
    plt.xlabel("epoch")
    plt.ylabel("log loss")
    plt.show()


def plot_sample(x, y, axis):
    img = x.reshape(H, W)
    axis.imshow(img, cmap='gray')
    axis.scatter(y[0::2] * H, y[1::2] * W, marker='x', s=10)

def save_model(model, name):
    '''
    save model architecture and model weights
    '''
    json_string = model.to_json()

    with open(name + '_architecture.json', 'w') as json_file:
        json_file.write(json_string)
    model.save_weights(name + '_weights.h5')

    print("Saved model to disk")


def load_model(name):
    model = model_from_json(open(name + '_architecture.json').read())
    model.load_weights(name + '_weights.h5')
    return model


def prepare_data():
    # Create train data
    if exists(X_TRAIN_PATH) and exists(Y_TRAIN_PATH) and exists(X_TEST_PATH) and exists(Y_TEST_PATH):
        X_train, Y_train = np.load(X_TRAIN_PATH, allow_pickle=True), np.load(Y_TRAIN_PATH, allow_pickle=True)
        X_test, Y_test = np.load(X_TEST_PATH, allow_pickle=True), np.load(Y_TEST_PATH, allow_pickle=True)

    else:
        df = pd.read_csv('Data/training.csv', header=0, sep=',', quotechar='"')

        X = []
        Y = []

        for index, row in df.iterrows():

            print(index)

            values = []
            nan = False
            for value in row.values[:30]:
                if np.isnan(value):
                    nan = True
                    break

                values.append(value if not np.isnan(value) else 0)

            if nan:
                continue

            sample = np.reshape(a=np.array(row['Image'].split(' ')), newshape=(H, W, 1))
            sample_annotation = row.values[:30]

            X.append(sample)
            Y.append(sample_annotation)

        X = np.array(X).astype(np.float32) / 255
        Y = np.array(Y).astype(np.float32) / 96

        X_train, X_test, Y_train, Y_test = train_test_split(X, Y)

        np.save(X_TRAIN_PATH, X_train)
        np.save(Y_TRAIN_PATH, Y_train)
        np.save(X_TEST_PATH, X_test)
        np.save(Y_TEST_PATH, Y_test)

    print(X_train.shape)
    print(Y_train.shape)
    print(X_test.shape)
    print(Y_test.shape)

    return X_train, X_test, Y_train.astype(np.float32), Y_test.astype(np.float32)


def prepare_model(dropout=True):
    """
        * We wont always want dropout, because, for example, too high a dropout rate can slow the convergence rate of the model,
        and often hurt final performance.

        * Adding batch normalization to prevent cases where some large weights over influence other weights (similar to normalizing data prior to training)
        and to improve training speed.

        * Adding another fully connected layer in order to extract semantically stronger global relationship between the features.

    :param dropout:
    :return:
    """
    model = Sequential()

    # ============================= #
    # Feature Extraction
    # ============================= #
    model.add(Conv2D(32, kernel_size=5, padding='same', kernel_initializer=keras.initializers.RandomNormal(mean=0.0, stddev=0.05, seed=None), input_shape=(H, W, 1)))
    model.add(MaxPooling2D(pool_size=(2, 2)))
    model.add(Activation('relu'))
    model.add(BatchNormalization())
    if dropout:
        model.add(Dropout(0.25))

    model.add(Conv2D(64, kernel_size=5,kernel_initializer=keras.initializers.RandomNormal(mean=0.0, stddev=0.05, seed=None), padding='same'))
    model.add(MaxPooling2D(pool_size=(2, 2)))
    model.add(Activation('relu'))
    model.add(BatchNormalization())
    if dropout:
        model.add(Dropout(0.25))

    model.add(Conv2D(128, kernel_size=5, kernel_initializer=keras.initializers.RandomNormal(mean=0.0, stddev=0.05, seed=None),padding='same'))
    model.add(MaxPooling2D(pool_size=(2, 2)))
    model.add(Activation('relu'))
    model.add(BatchNormalization())
    if dropout:
        model.add(Dropout(0.25))

    # ============================= #
    # Keypoint Extraction
    # ============================= #
    model.add(Flatten())

    model.add(Dense(500))
    model.add(Activation('relu'))
    if Dropout:
        model.add(Dropout(0.25))

    model.add(Dense(500))
    model.add(Activation('relu'))
    if Dropout:
        model.add(Dropout(0.25))

    model.add(Dense(30))
    model.add(Activation('sigmoid'))

    model.compile(optimizer="adam", loss='mean_squared_error', metrics=["accuracy"])

    model.summary()

    return model


def main():
    X_train, X_test, Y_train, Y_test = prepare_data()
    model = prepare_model()

    hist = model.fit(x=X_train, y=Y_train, epochs=1000, validation_data=(X_test, Y_test))
    plot_loss(hist.history, "model 1", plt)

    save_model(model, "Models/model_0")

